CREATE TABLE "users" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "first_name" varchar NOT NULL,
  "last_name" varchar NOT NULL,
  "email" varchar NOT NULL,
  "password" varchar NOT NULL,
  "phone" varchar,
  "birthday" date DEFAULT '2000/01/01'
);

CREATE TABLE "coursers" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "title" varchar NOT NULL,
  "description" varchar NOT NULL,
  "level" varchar NOT NULL,
  "teacher" varchar NOT NULL,
  "id_categories" int
);

CREATE TABLE "courser_video" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "title" varchar NOT NULL,
  "video_url" varchar NOT NULL,
  "id_courser" int
);

CREATE TABLE "categories" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "name_categoria" varchar NOT NULL
);

CREATE TABLE "users_courser" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "id_courser" int,
  "id_user" int
);

ALTER TABLE "users_courser" ADD FOREIGN KEY ("id_user") REFERENCES "users" ("id");

ALTER TABLE "users_courser" ADD FOREIGN KEY ("id_courser") REFERENCES "coursers" ("id");

ALTER TABLE "coursers" ADD FOREIGN KEY ("id_categories") REFERENCES "categories" ("id");

ALTER TABLE "courser_video" ADD FOREIGN KEY ("id_courser") REFERENCES "coursers" ("id");

